SELECT a.id, a.title, a.cover_image AS coverImage, c.name AS categoryName, a.is_top AS isTop, a.view_count AS viewCount, a.like_count AS likeCount, a.create_time AS createTime FROM article a LEFT JOIN category c ON c.id = a.category_id AND c.deleted = 0
a.deleted = 0
AND a.status = #{queryDto.status}
AND a.category_id = #{queryDto.categoryId}
AND a.id IN ( SELECT at3.article_id FROM article_tag at3 JOIN tag t3 ON t3.id = at3.tag_id AND t3.deleted = 0 WHERE at3.tag_id IN #{tid} GROUP BY at3.article_id HAVING COUNT(DISTINCT at3.tag_id) = #{tagCount} )
ORDER BY a.is_top DESC, a.create_time DESC

SELECT at.article_id AS articleId, t.name AS tagName FROM article_tag at JOIN tag t ON t.id = at.tag_id AND t.deleted = 0
at.article_id IN
#{id}

SELECT a.`id`, u.`username` AS `username`, a.`title` AS `title`, a.`view_count` AS `view_count`, a.`like_count` AS `like_count`, a.`create_time` AS `create_time` FROM `article` a LEFT JOIN `user` u ON u.`id` = a.`user_id`
a.`deleted` = 0 AND a.`user_id` = #{userId}
AND a.`status` = #{queryDto.status}
AND a.`category_id` = #{queryDto.categoryId}
AND a.`id` IN ( SELECT at3.`article_id` FROM `article_tag` at3 JOIN `tag` t3 ON t3.`id` = at3.`tag_id` AND t3.`deleted` = 0 WHERE at3.`tag_id` IN
#{tid}
GROUP BY at3.`article_id` HAVING COUNT(DISTINCT at3.`tag_id`) = #{tagCount} )
ORDER BY a.`create_time` DESC, a.`id` DESC
